import React, {useContext, useState} from "react";
import {AppContext} from "../context/AppContext";

const ListExportedProducts = () => {
    const {products} = useContext(AppContext)

    const exportedProducts = products.filter((product) => product.exportedQuantity > 0)
    if (!products){
        return (
            <p>No existen productos ya exportados</p>
        )
    }
    return (
        <>
            <h2>Productos exportados</h2>
            {exportedProducts.map((product, i) => (
               <div className='col-xl-3' key={i}>
                    <div className="card">
                        <img className="card-img-top" src={product.image} alt={product.name}
                             style={{width: '270px', height: '150px', margin:'auto', marginTop:'10px'}}/>
                        <div className="card-body">
                            <h4 className="card-title">{product.name}</h4>
                            <h5>Cantidad exportada: {product.exportedQuantity}</h5>
                            <div className='row'>
                                <div className='col-6'>
                                    <p className={'card-text'}>{product.nativeCountry}</p>
                                </div>
                                <div className='col-6'>
                                    <p className="card-text">{product.type}</p>
                                </div>
                            </div>
                            <div className={'row'}>
                                {product.exportedTo.map((country) => (
                                    <div className={'col-4'}>
                                        <p>{country.name}: {country.quantity}</p>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            ))}
        </>
    )
}
export default ListExportedProducts
